newCode:
@media (max-width: 768px) {
  .app-table {display: block; overflow-x: auto; white-space: nowrap}
  .app-table th, .app-table td {padding: 10px 8px}
}

/* ===== APPLICATIONS MANAGEMENT PREMIUM UPGRADE ===== */

/* Stats Dashboard */
.apps-stats-dashboard {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
  margin-bottom: 20px;
  background: linear-gradient(135deg, rgba(91,157,255,0.08), rgba(50,230,133,0.05));
  padding: 16px;
  border-radius: 12px;
  border: 1px solid var(--ring);
  animation: slideIn 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.stat-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  padding: 12px;
  background: var(--panel);
  border: 1px solid var(--ring);
  border-radius: 10px;
  transition: all 0.3s ease;
  cursor: default;
}

.stat-card:hover {
  transform: translateY(-2px);
  border-color: var(--primary);
  background: linear-gradient(160deg, var(--glass), transparent), var(--panel);
  box-shadow: 0 4px 12px rgba(91,157,255,0.15);
}

.stat-label {
  font-size: 11px;
  color: var(--muted);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  font-weight: 700;
}

.stat-value {
  font-size: 20px;
  font-weight: 800;
  color: var(--primary);
  line-height: 1;
}

.stat-icon {
  font-size: 16px;
  opacity: 0.7;
}

/* Premium Search & Filter Bar */
.apps-search-filter-bar {
  display: flex;
  gap: 12px;
  margin-bottom: 16px;
  flex-wrap: wrap;
  align-items: center;
}

.apps-search-input {
  flex: 1;
  min-width: 200px;
  appearance: none;
  background: var(--panel);
  border: 1px solid var(--ring);
  color: var(--text);
  padding: 10px 14px;
  border-radius: 8px;
  font-size: 13px;
  transition: all 0.3s ease;
  backdrop-filter: blur(2px);
}

.apps-search-input::placeholder {
  color: var(--muted);
  opacity: 0.5;
}

.apps-search-input:focus {
  outline: none;
  border-color: var(--primary);
  background: linear-gradient(160deg, var(--glass), transparent), var(--panel);
  box-shadow: 0 0 12px rgba(91,157,255,0.2);
}

.apps-filter-button {
  appearance: none;
  background: var(--panel);
  border: 1px solid var(--ring);
  color: var(--text);
  padding: 10px 12px;
  border-radius: 8px;
  font-size: 13px;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 6px;
  white-space: nowrap;
}

.apps-filter-button:hover {
  border-color: var(--primary);
  background: linear-gradient(160deg, var(--glass), transparent), var(--panel);
  color: var(--primary);
}

.apps-filter-button.active {
  background: var(--primary);
  border-color: var(--primary);
  color: var(--bg);
  font-weight: 600;
}

/* Active Filters Display */
.apps-active-filters {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  margin-bottom: 16px;
  align-items: center;
  min-height: 28px;
}

.apps-active-filters.hidden {
  display: none;
}

.filter-badge {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  background: linear-gradient(135deg, var(--primary), rgba(91,157,255,0.7));
  color: white;
  padding: 6px 10px;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 600;
  animation: popIn 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.filter-badge-remove {
  cursor: pointer;
  opacity: 0.7;
  transition: opacity 0.2s ease;
  font-weight: 700;
  padding: 0 2px;
}

.filter-badge-remove:hover {
  opacity: 1;
}

/* Premium Table Header */
.apps-table-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 16px;
  background: linear-gradient(90deg, rgba(91,157,255,0.05), rgba(50,230,133,0.03));
  border-bottom: 1px solid var(--ring);
  margin-bottom: 0;
}

.apps-table-info {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 13px;
  color: var(--muted);
}

.apps-table-info-icon {
  font-size: 16px;
}

.apps-count-display {
  font-weight: 700;
  color: var(--primary);
}

/* Enhanced Table Container */
.apps-table-container-premium {
  border: 1px solid var(--ring);
  border-radius: 10px;
  overflow: hidden;
  background: var(--bg);
  box-shadow: 0 2px 8px rgba(0,0,0,0.04);
  animation: fadeIn 0.4s ease-out;
}

/* Enhanced Table Styling */
.app-table-premium {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  background: var(--bg);
}

.app-table-premium th {
  text-align: left;
  padding: 14px 16px;
  border-bottom: 2px solid var(--ring);
  font-size: 12px;
  color: var(--text);
  font-weight: 700;
  background: linear-gradient(180deg, var(--bg-2), rgba(14, 22, 39, 0.9));
  cursor: pointer;
  user-select: none;
  transition: all 0.2s ease;
  position: sticky;
  top: 0;
  z-index: 5;
  text-transform: uppercase;
  letter-spacing: 0.3px;
}

.app-table-premium th:hover {
  background: linear-gradient(180deg, rgba(91,157,255,0.12), rgba(14, 22, 39, 1));
  color: var(--primary);
}

.app-table-premium td {
  padding: 12px 16px;
  border-bottom: 1px solid var(--ring);
  font-size: 13px;
  vertical-align: middle;
}

.app-table-premium tr {
  background-color: var(--bg);
  transition: all 0.2s ease;
}

.app-table-premium tbody tr:hover {
  background: var(--ring);
  box-shadow: inset 0 0 10px rgba(91,157,255,0.05);
}

.app-table-premium tbody tr:active {
  background: rgba(91,157,255,0.1);
}

/* Cell Styling */
.app-table-premium input[type=text],
.app-table-premium input[type=number],
.app-table-premium select {
  appearance: none;
  background: var(--bg-2);
  border: 1px solid var(--ring);
  color: var(--text);
  padding: 8px 10px;
  border-radius: 6px;
  font-size: 12px;
  width: 100%;
  transition: all 0.2s ease;
  font-family: inherit;
  cursor: pointer;
}

.app-table-premium input:focus,
.app-table-premium select:focus {
  outline: none;
  border-color: var(--primary);
  background: var(--panel);
  box-shadow: 0 0 8px rgba(91,157,255,0.25), inset 0 0 4px rgba(91,157,255,0.1);
}

/* Cell-specific Styling */
.cell-status-badge {
  padding: 6px 10px !important;
  border-radius: 8px !important;
  font-weight: 600 !important;
  font-size: 13px !important;
  display: inline-block !important;
  animation: slideIn 0.3s ease-out;
}

.cell-status-badge[data-status="TBS"] {
  background: rgba(255, 95, 122, 0.15);
  color: #ff5f7a;
  border: 1px solid rgba(255, 95, 122, 0.3);
}

.cell-status-badge[data-status="WIP"] {
  background: rgba(255, 209, 102, 0.15);
  color: #ffd166;
  border: 1px solid rgba(255, 209, 102, 0.3);
}

.cell-status-badge[data-status="CLO"] {
  background: rgba(50, 230, 133, 0.15);
  color: #32e685;
  border: 1px solid rgba(50, 230, 133, 0.3);
}

/* Progress Cell Premium */
.progress-cell-premium {
  position: relative;
  display: flex;
  align-items: center;
  gap: 8px;
}

.progress-bar-mini {
  position: relative;
  flex: 1;
  height: 24px;
  background: var(--ring);
  border-radius: 6px;
  overflow: hidden;
  min-width: 80px;
  border: 1px solid var(--ring);
}

.progress-bar-fill {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  background: linear-gradient(90deg, var(--primary), rgba(91,157,255,0.5));
  border-radius: 5px;
  transition: width 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 11px;
  font-weight: 700;
  color: white;
  opacity: 0;
}

.progress-bar-fill.show-label {
  opacity: 1;
}

/* Weight Display */
.auto-weight {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, rgba(91,157,255,0.15), rgba(50,230,133,0.1));
  padding: 6px 10px;
  border-radius: 6px;
  font-weight: 700;
  color: var(--primary);
  border: 1px solid rgba(91,157,255,0.2);
  font-size: 12px;
  min-width: 45px;
  text-align: center;
}

/* Empty State */
.apps-empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 48px 24px;
  text-align: center;
  color: var(--muted);
  background: linear-gradient(135deg, rgba(91,157,255,0.03), rgba(50,230,133,0.02));
}

.apps-empty-state-icon {
  font-size: 48px;
  margin-bottom: 16px;
  opacity: 0.6;
  animation: fadeIn 0.5s ease-out;
}

.apps-empty-state-title {
  font-size: 16px;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 8px;
}

.apps-empty-state-description {
  font-size: 13px;
  color: var(--muted);
  margin-bottom: 16px;
}

/* Action Buttons */
.app-actions-cell {
  display: flex;
  gap: 8px;
  align-items: center;
}

.btn-app-action {
  appearance: none;
  background: var(--bg-2);
  border: 1px solid var(--ring);
  color: var(--text);
  padding: 6px 10px;
  border-radius: 6px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  white-space: nowrap;
  font-weight: 500;
}

.btn-app-action:hover {
  transform: translateY(-1px);
  box-shadow: 0 2px 6px rgba(0,0,0,0.1);
}

.btn-app-action.reassign {
  border-color: var(--primary);
  color: var(--primary);
}

.btn-app-action.reassign:hover {
  background: rgba(91,157,255,0.1);
}

.btn-app-action.delete {
  border-color: var(--danger);
  color: var(--danger);
}

.btn-app-action.delete:hover {
  background: rgba(255,95,122,0.1);
}

/* Animations for Applications Management */
@keyframes slideInTable {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes highlightRow {
  0% { background: var(--ring); }
  100% { background: var(--bg); }
}

/* Responsive Applications Table */
@media (max-width: 1024px) {
  .apps-table-container-premium {
    overflow-x: auto;
  }

  .app-table-premium {
    min-width: 800px;
  }

  .apps-search-filter-bar {
    flex-direction: column;
  }

  .apps-search-input {
    min-width: 100%;
  }
}

@media (max-width: 768px) {
  .apps-stats-dashboard {
    grid-template-columns: repeat(2, 1fr);
    gap: 8px;
    padding: 12px;
  }

  .stat-card {
    padding: 10px;
  }

  .stat-label {
    font-size: 10px;
  }

  .stat-value {
    font-size: 18px;
  }

  .app-table-premium th,
  .app-table-premium td {
    padding: 10px 12px;
    font-size: 12px;
  }

  .app-table-premium input[type=text],
  .app-table-premium input[type=number],
  .app-table-premium select {
    font-size: 11px;
    padding: 6px 8px;
  }

  .btn-app-action {
    padding: 5px 8px;
    font-size: 11px;
  }
}

/* ===== APPLICATIONS OVERVIEW PREMIUM UPGRADE ===== */
