/* ========================================
   APPLICATIONS OVERVIEW - WORLD CLASS STYLES
   ======================================== */

/* KPI DASHBOARD - Enhanced Metrics */
.kpi-dashboard-world-class {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 16px;
  margin-bottom: 28px;
  animation: fadeIn 0.5s ease-out;
}

.kpi-card {
  background: linear-gradient(135deg, var(--panel) 0%, rgba(20, 30, 48, 0.8) 100%);
  border: 1px solid var(--ring);
  border-radius: 16px;
  padding: 20px;
  display: flex;
  align-items: center;
  gap: 16px;
  transition: all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1);
  cursor: pointer;
  position: relative;
  overflow: hidden;
}

.kpi-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
  transition: left 0.5s;
}

.kpi-card:hover {
  border-color: #5b9dff;
  box-shadow: 0 8px 24px rgba(91, 157, 255, 0.2);
  transform: translateY(-2px);
}

.kpi-card:hover::before {
  left: 100%;
}

.kpi-icon {
  font-size: 32px;
  line-height: 1;
}

.kpi-content {
  flex: 1;
}

.kpi-label {
  font-size: 12px;
  color: #aaa;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 6px;
}

.kpi-value {
  font-size: 28px;
  font-weight: 700;
  color: var(--text);
  line-height: 1;
  margin-bottom: 4px;
}

.kpi-trend {
  font-size: 11px;
  color: #888;
  font-style: italic;
}

.kpi-mini-bars {
  display: flex;
  gap: 12px;
  margin-top: 8px;
}

.mini-bar-item {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 12px;
}

.mini-label {
  color: #aaa;
  font-weight: 600;
}

.mini-value {
  color: #5b9dff;
  font-weight: 700;
  min-width: 20px;
  text-align: right;
}

/* ADVANCED CONTROLS */
.advanced-controls-section {
  background: rgba(14, 22, 39, 0.5);
  border: 1px solid var(--ring);
  border-radius: 12px;
  padding: 16px;
  margin-bottom: 20px;
}

.controls-top-row {
  display: flex;
  gap: 16px;
  align-items: center;
  flex-wrap: wrap;
  margin-bottom: 12px;
}

.filter-buttons-group {
  display: flex;
  gap: 8px;
}

.search-box-wrapper {
  flex: 1;
  min-width: 250px;
}

/* STATUS FILTER PILLS */
.status-filters-row {
  display: flex;
  gap: 8px;
  align-items: center;
  flex-wrap: wrap;
}

.filter-label {
  font-size: 12px;
  color: #aaa;
  font-weight: 600;
  text-transform: uppercase;
}

.status-pill-filter {
  padding: 6px 14px;
  border: 1px solid var(--ring);
  background: transparent;
  color: #aaa;
  border-radius: 20px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  white-space: nowrap;
}

.status-pill-filter:hover {
  border-color: #5b9dff;
  color: #5b9dff;
  background: rgba(91, 157, 255, 0.1);
}

.status-pill-filter.active {
  background: linear-gradient(135deg, #5b9dff 0%, #4a7dd6 100%);
  border-color: #5b9dff;
  color: white;
  box-shadow: 0 4px 12px rgba(91, 157, 255, 0.3);
}

/* MINI CHARTS GRID */
.mini-charts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  margin-bottom: 20px;
}

.mini-chart-panel {
  background: linear-gradient(135deg, rgba(14, 22, 39, 0.6), rgba(20, 30, 48, 0.6));
  border: 1px solid var(--ring);
  border-radius: 12px;
  padding: 16px;
  transition: all 0.3s ease;
}

.mini-chart-panel:hover {
  border-color: rgba(91, 157, 255, 0.3);
  background: linear-gradient(135deg, rgba(14, 22, 39, 0.8), rgba(25, 35, 55, 0.8));
}

.mini-chart-header {
  font-size: 12px;
  font-weight: 600;
  color: #aaa;
  margin-bottom: 12px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.mini-chart-container {
  height: 80px;
  display: flex;
  gap: 2px;
  align-items: flex-end;
}

.wave-dist, .status-breakdown {
  gap: 4px;
}

/* COMPLETION GRADIENT */
.completion-gradient {
  height: 60px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
}

.gradient-bar {
  width: 100%;
  height: 32px;
  background: linear-gradient(90deg, #ff5f7a 0%, #ffd166 50%, #32e685 100%);
  border-radius: 8px;
  position: relative;
}

.gradient-labels {
  display: flex;
  justify-content: space-between;
  margin-top: 8px;
  font-size: 10px;
  color: #888;
  font-weight: 600;
}

/* TABLE ENHANCEMENTS */
.table-empty-state {
  padding: 60px 20px;
  text-align: center;
}

.empty-state-icon {
  font-size: 48px;
  margin-bottom: 12px;
  opacity: 0.6;
}

.empty-state-title {
  font-size: 16px;
  font-weight: 600;
  color: var(--text);
  margin-bottom: 6px;
}

.empty-state-description {
  font-size: 12px;
  color: #888;
}

/* ROW HIGHLIGHTING */
tr.complete-row {
  background: rgba(50, 230, 133, 0.05);
  border-left: 3px solid #32e685;
}

tr.complete-row:hover {
  background: rgba(50, 230, 133, 0.1);
}

tr.at-risk-row {
  background: rgba(255, 209, 102, 0.05);
  border-left: 3px solid #ffd166;
}

tr.at-risk-row:hover {
  background: rgba(255, 209, 102, 0.1);
}

tr.critical-row {
  background: rgba(255, 95, 122, 0.08);
  border-left: 3px solid #ff5f7a;
}

tr.critical-row:hover {
  background: rgba(255, 95, 122, 0.12);
}

/* FACTOR BADGES */
.factors-cell {
  display: flex;
  flex-wrap: wrap;
  gap: 4px;
}

.factor-badge {
  display: inline-block;
  padding: 3px 8px;
  border-radius: 6px;
  font-size: 10px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.3px;
}

.factor-badge.critical {
  background: rgba(255, 95, 122, 0.2);
  color: #ff5f7a;
}

.factor-badge.impact {
  background: rgba(255, 209, 102, 0.2);
  color: #ffd166;
}

.factor-badge.priority {
  background: rgba(91, 157, 255, 0.2);
  color: #5b9dff;
}

/* PROGRESS SPARKLINE */
.progress-sparkline {
  display: flex;
  align-items: center;
  gap: 8px;
}

.progress-bar-container {
  flex: 1;
  height: 6px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 3px;
  overflow: hidden;
  position: relative;
}

.progress-bar-fill {
  height: 100%;
  background: linear-gradient(90deg, #5b9dff 0%, #32e685 100%);
  border-radius: 3px;
  transition: width 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  animation: shimmer 2s infinite;
}

@keyframes shimmer {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.9; }
}

.progress-value {
  font-size: 11px;
  font-weight: 600;
  color: #aaa;
  min-width: 35px;
  text-align: right;
}

/* STATUS BADGE INLINE */
.status-badge-inline {
  display: inline-block;
  padding: 4px 10px;
  border-radius: 8px;
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
}

.status-badge-inline.clo {
  background: rgba(50, 230, 133, 0.2);
  color: #32e685;
}

.status-badge-inline.wip {
  background: rgba(255, 209, 102, 0.2);
  color: #ffd166;
}

.status-badge-inline.tbs {
  background: rgba(255, 95, 122, 0.2);
  color: #ff5f7a;
}

/* WAVE BADGES */
.wave-badge {
  display: inline-block;
  padding: 4px 10px;
  border-radius: 8px;
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
}

.wave-1 {
  background: rgba(91, 157, 255, 0.2);
  color: #5b9dff;
}

.wave-2 {
  background: rgba(255, 209, 102, 0.2);
  color: #ffd166;
}

.wave-3 {
  background: rgba(50, 230, 133, 0.2);
  color: #32e685;
}

/* TABLE ANIMATIONS */
#appsOverviewTableBody tr {
  animation: rowFadeIn 0.4s ease-out;
}

#appsOverviewTableBody tr:nth-child(n) {
  animation-delay: calc(0.05s * var(--row-index, 1));
}

@keyframes rowFadeIn {
  from {
    opacity: 0;
    transform: translateY(-4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

/* RESPONSIVE DESIGN */
@media (max-width: 1200px) {
  .kpi-dashboard-world-class {
    grid-template-columns: repeat(2, 1fr);
  }

  .mini-charts-grid {
    grid-template-columns: 1fr;
  }

  .controls-top-row {
    flex-direction: column;
    align-items: flex-start;
  }

  .search-box-wrapper {
    width: 100%;
  }
}

@media (max-width: 768px) {
  .kpi-dashboard-world-class {
    grid-template-columns: 1fr;
  }

  .kpi-card {
    padding: 16px;
  }

  .kpi-value {
    font-size: 24px;
  }

  .advanced-controls-section {
    padding: 12px;
  }

  .controls-top-row {
    flex-direction: column;
  }

  .search-box-wrapper {
    width: 100%;
  }

  .status-filters-row {
    gap: 4px;
  }

  .status-pill-filter {
    padding: 4px 10px;
    font-size: 11px;
  }

  #appsOverviewTable {
    font-size: 12px;
  }

  #appsOverviewTable th {
    font-size: 11px;
  }

  .mini-chart-container {
    height: 60px;
  }
}

/* ========================================
   END APPLICATIONS OVERVIEW STYLES
   ======================================== */

operations.scriptContent:
/* ===== APPLICATIONS OVERVIEW - WORLD CLASS LOGIC ===== */

const AppsOverviewWorldClass = {

  currentFilter: { wave: null, status: null, search: '' },
  allAppsData: [],

  init() {
    console.log('[AppsOverviewWorldClass] Initializing...');
    this.bindEvents();
    this.refresh();
  },

  bindEvents() {
    const searchInput = document.getElementById('appsOverviewSearch');
    if (searchInput) {
      searchInput.addEventListener('input', (e) => {
        this.currentFilter.search = e.target.value.toLowerCase();
        this.render();
      });
    }

    document.querySelectorAll('.status-pill-filter').forEach(btn => {
      btn.addEventListener('click', (e) => {
        document.querySelectorAll('.status-pill-filter').forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        this.currentFilter.status = e.target.dataset.status === 'all' ? null : e.target.dataset.status;
        this.render();
      });
    });

    document.querySelector('.status-pill-filter[data-status="all"]').classList.add('active');
  },

  refresh() {
    try {
      const config = Dashboard.StorageManager.loadConfig();
      this.allAppsData = (config.apps || []).map(app => {
        const bu = (config.buses || []).find(b => b.id === app.buId);
        return { ...app, buName: bu?.name || 'N/A', buKey: bu?.key || 'N/A' };
      });
      this.render();
    } catch (error) {
      console.error('[AppsOverviewWorldClass] Error:', error);
    }
  },

  render() {
    const filtered = this.filterData();
    this.updateMetrics(filtered);
    this.updateCharts(filtered);
    this.renderTable(filtered);
  },

  filterData() {
    return this.allAppsData.filter(app => {
      if (this.currentFilter.search) {
        const search = this.currentFilter.search;
        if (![
          app.name?.toLowerCase(),
          app.buName?.toLowerCase(),
          app.buKey?.toLowerCase(),
          app.id?.toLowerCase()
        ].some(v => v?.includes(search))) return false;
      }
      if (this.currentFilter.status && app.status !== this.currentFilter.status) return false;
      if (this.currentFilter.wave && app.wave !== this.currentFilter.wave) return false;
      return true;
    });
  },

  updateMetrics(filtered) {
    const total = this.allAppsData.length;
    const completion = filtered.length > 0 ? Math.round(filtered.reduce((sum, a) => sum + (a.progress || 0), 0) / filtered.length) : 0;
    const atRisk = filtered.filter(a => (a.progress || 0) < 50).length;
    const w1 = filtered.filter(a => a.wave === 'Wave 1').length;
    const w2 = filtered.filter(a => a.wave === 'Wave 2').length;
    const w3 = filtered.filter(a => a.wave === 'Wave 3').length;

    document.getElementById('statsTotalApps').textContent = total;
    document.getElementById('statsCompletionPct').textContent = completion;
    document.getElementById('statsAtRisk').textContent = atRisk;
    document.getElementById('waveCount1').textContent = w1;
    document.getElementById('waveCount2').textContent = w2;
    document.getElementById('waveCount3').textContent = w3;

    if (filtered.length > 0) {
      const progressValues = filtered.map(a => a.progress || 0);
      document.getElementById('completionMin').textContent = Math.min(...progressValues);
      document.getElementById('completionMax').textContent = Math.max(...progressValues);
    }

    document.getElementById('appsCountDisplay').textContent = filtered.length;
    document.getElementById('appsTotalDisplay').textContent = total;
  },

  updateCharts(filtered) {
    if (filtered.length > 0) {
      const w1 = filtered.filter(a => a.wave === 'Wave 1').length;
      const w2 = filtered.filter(a => a.wave === 'Wave 2').length;
      const w3 = filtered.filter(a => a.wave === 'Wave 3').length;
      const max = Math.max(w1, w2, w3, 1);

      const waveChart = document.getElementById('waveDistChart');
      if (waveChart) {
        waveChart.innerHTML = `
          <div style="flex: 1; background: rgba(50,230,133,0.3); height: ${Math.round(w1/max*100)}%; border-radius: 2px; min-height: 4px;" title="Wave 1: ${w1}"></div>
          <div style="flex: 1; background: rgba(255,209,102,0.3); height: ${Math.round(w2/max*100)}%; border-radius: 2px; min-height: 4px;" title="Wave 2: ${w2}"></div>
          <div style="flex: 1; background: rgba(91,157,255,0.3); height: ${Math.round(w3/max*100)}%; border-radius: 2px; min-height: 4px;" title="Wave 3: ${w3}"></div>
        `;
      }

      const clo = filtered.filter(a => a.status === 'CLO').length;
      const wip = filtered.filter(a => a.status === 'WIP').length;
      const tbs = filtered.filter(a => a.status === 'TBS').length;
      const statusMax = Math.max(clo, wip, tbs, 1);

      const statusChart = document.getElementById('statusBreakChart');
      if (statusChart) {
        statusChart.innerHTML = `
          <div style="flex: 1; background: rgba(50,230,133,0.4); border-radius: 2px; position: relative;" title="Complete: ${clo}"><span style="position: absolute; bottom: -18px; font-size: 10px; color: #32e685; left: 0; right: 0; text-align: center;">${clo}</span></div>
          <div style="flex: 1; background: rgba(255,209,102,0.4); border-radius: 2px; position: relative;" title="In Progress: ${wip}"><span style="position: absolute; bottom: -18px; font-size: 10px; color: #ffd166; left: 0; right: 0; text-align: center;">${wip}</span></div>
          <div style="flex: 1; background: rgba(255,95,122,0.4); border-radius: 2px; position: relative;" title="To Start: ${tbs}"><span style="position: absolute; bottom: -18px; font-size: 10px; color: #ff5f7a; left: 0; right: 0; text-align: center;">${tbs}</span></div>
        `;
      }
    }
  },

  renderTable(filtered) {
    const tbody = document.getElementById('appsOverviewTableBody');
    const emptyState = document.getElementById('emptyState');
    if (!tbody) return;

    if (filtered.length === 0) {
      tbody.innerHTML = '';
      emptyState.style.display = 'block';
      return;
    }

    emptyState.style.display = 'none';
    tbody.innerHTML = filtered.map(app => {
      const isAtRisk = (app.progress || 0) < 50 && app.status !== 'CLO';
      const isCritical = (app.progress || 0) < 30 && app.status === 'WIP';
      const rowClass = isCritical ? 'critical-row' : isAtRisk ? 'at-risk-row' : 'complete-row';
      const statusEmoji = app.status === 'CLO' ? '‚úì' : app.status === 'WIP' ? 'üîÑ' : '‚è≥';
      const waveClass = `wave-${app.wave?.split(' ')[1] || '1'}`;
      const factors = [
        app.criticality && { label: app.criticality, class: 'critical' },
        app.impact && { label: app.impact, class: 'impact' },
        app.priority && { label: app.priority, class: 'priority' }
      ].filter(Boolean);

      return `
        <tr class="${rowClass}">
          <td>${app.order || '-'}</td>
          <td>
            <div style="font-weight: 600; color: var(--text);">${app.name}</div>
            <div style="font-size: 11px; color: #aaa;">ID: ${app.id || '-'}</div>
          </td>
          <td><strong>${app.buKey}</strong></td>
          <td style="text-align: center;">
            <span class="wave-badge ${waveClass}">${app.wave || 'Wave 1'}</span>
          </td>
          <td style="text-align: center;">
            <strong style="color: #5b9dff;">${(app.weight || 0).toFixed(2)}</strong>
          </td>
          <td>
            <div class="factors-cell">
              ${factors.map(f => `<span class="factor-badge ${f.class?.toLowerCase()}">${f.label}</span>`).join('')}
            </div>
          </td>
          <td>
            <div class="progress-sparkline">
              <div class="progress-bar-container">
                <div class="progress-bar-fill" style="width: ${app.progress || 0}%"></div>
              </div>
              <span class="progress-value">${app.progress || 0}%</span>
            </div>
          </td>
          <td style="text-align: center;">
            <span class="status-badge-inline ${app.status?.toLowerCase() || 'tbs'}">
              ${statusEmoji} ${app.status || 'TBS'}
            </span>
          </td>
        </tr>
      `;
    }).join('');
  }
};

// Auto-initialize when modal tab is shown
const tabAppOverview = document.getElementById('tab-app-overview');
if (tabAppOverview) {
  const observer = new MutationObserver(() => {
    if (tabAppOverview.style.display !== 'none') {
      AppsOverviewWorldClass.init();
      observer.disconnect();
    }
  });
  observer.observe(tabAppOverview, { attributes: true });
}

window.AppsOverviewWorldClass = AppsOverviewWorldClass;
